(function($) {
    Drupal.behaviors.addIE10 = {
        attach: function(context, settings) {
            var $ua = window.navigator.userAgent;
            var $msie = $ua.indexOf("MSIE 10");
            if ($msie > 0) {
                $("html").addClass("ie10");
            }
        }
    };
    Drupal.behaviors.mobileMenu = {
        attach: function(context, settings) {
            $(".menu-btn").click(function() {
                $(".responsive-menu").toggle();
            });
        }
    };
})(jQuery);